package class01;

public class Test03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//모든 조건식은 F를 만나야 끝난다
		for(int a=0; a<5; a++) { // 세로로 얼마나
			for(int i=0; i<=a; i++) {// 가로로 얼마나
				System.out.print("*");
			}
			System.out.println();
		}

	}

}
